import React, { useState } from 'react';
import './FlightSearch.css';
import Flights from './Flights';

function FlightSearch() { 
    const [quotes,setQuotes] = useState([])
    const [originPlace,setOriginPlace] = useState("")
    const [destinationPlace,setDestinationPlace] = useState("")
    const [outboundPartialDate,setOutboundPartialDate] = useState("")
    const [inboundPartialDate,setInboundPartialDate] = useState("")
    const [showQuotes,setShowQuotes] = useState(false)

    function handleSubmit(e) {
        e.preventDefault()
        async function fetchMyAPI() {
            const reqOptions = {
                method: 'GET',
                headers: {
                    "x-rapidapi-key": '8e6b478235msh794d79dac0c98e3p1b6440jsnb32c209b38dc',
	                "x-rapidapi-host": "skyscanner-skyscanner-flight-search-v1.p.rapidapi.com",
	                "useQueryString": true
                }
            }
            let response = await fetch('https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browsequotes/v1.0/US/USD/en-US/{originPlace}/{destinationPlace}/{outboundPartialDate}' , reqOptions)
            response = await response.json()
            setQuotes(response.Quotes)
        }
        fetchMyAPI()
        console.log(quotes)
        setShowQuotes(true)
        setOriginPlace("")
        setDestinationPlace("")
        setOutboundPartialDate("")
        setInboundPartialDate("")
    }

    return(
        <div className="flightsearch">
           <form onSubmit={handleSubmit}>
                <label htmlFor="originPlace">From: </label>
                <input id="originPlace" value={originPlace} onChange={e => setOriginPlace(e.target.value)} required/>
                <label htmlFor="destinationPlace"> To: </label>
                <input id="destinationPlace" value={destinationPlace} onChange={e => setDestinationPlace(e.target.value)} required/>
                <label htmlFor="outboundPartialDate"> Outbound: </label>
                <input id="outboundPartialDate" value={outboundPartialDate} onChange={e => setOutboundPartialDate(e.target.value)} required/>
                <label htmlFor="inboundPartialDate"> Inbound: </label>
                <input id="outboundPartialDate" value={inboundPartialDate} onChange={e => setInboundPartialDate(e.target.value)} />
                <button className="search">Submit</button>
           </form>
           { showQuotes ? <Flights quotes={quotes}></Flights> : <></>}
        </div>
    )
}

export default FlightSearch;
