import React, { useState } from 'react';
import './FlightSearch.css';
import Flights from './Flights';

// get api key from env file
const API_KEY = process.env.REACT_APP_API_KEY;

function FlightSearch() { 
    const [flightInfo,setFlightInfo] = undefined
    const [originPlace,setOriginPlace] = useState("")
    const [destinationPlace,setDestinationPlace] = useState("")
    const [outboundPartialDate,setOutboundPartialDate] = useState("")
    const [inboundPartialDate,setInboundPartialDate] = useState("")
    const [showQuotes,setShowQuotes] = useState(false)

    function handleSubmit(e) {
        e.preventDefault()
        async function fetchMyAPI() {
            const reqOptions = {
                method: 'GET',
                headers: {
                    "x-rapidapi-key": String(API_KEY),
	                "x-rapidapi-host": "skyscanner-skyscanner-flight-search-v1.p.rapidapi.com",
	                "useQueryString": true
                }
            }
            let response = await fetch(`https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browsequotes/v1.0/US/USD/en-US/${encodeURIComponent(originPlace)}/${encodeURIComponent(destinationPlace)}/${encodeURIComponent(outboundPartialDate)}`,reqOptions);
            const result = await response.json()
            setFlightInfo(result)
        }
        fetchMyAPI()
        console.log(flightInfo)
        setShowQuotes(true)
        setOriginPlace("")
        setDestinationPlace("")
        setOutboundPartialDate("")
        setInboundPartialDate("")
    }

    return(
        <div className="flightsearch">
           <form onSubmit={handleSubmit}>
                <label htmlFor="originPlace">From: </label>
                <input 
                    id="originPlace" 
                    value={originPlace} 
                    onChange={e => setOriginPlace(e.target.value)} 
                    required
                />
                <label htmlFor="destinationPlace"> To: </label>
                <input 
                    id="destinationPlace" 
                    value={destinationPlace} 
                    onChange={e => setDestinationPlace(e.target.value)} 
                    required
                />
                <label htmlFor="outboundPartialDate"> Outbound: </label>
                <input 
                    id="outboundPartialDate" 
                    value={outboundPartialDate} 
                    onChange={e => setOutboundPartialDate(e.target.value)} 
                    required
                />
                <label htmlFor="inboundPartialDate"> Inbound: </label>
                <input 
                    id="outboundPartialDate" 
                    value={inboundPartialDate} 
                    onChange={e => setInboundPartialDate(e.target.value)} 
                />
                <button className="search">Submit</button>
           </form>
           { showQuotes ? <Flights availableFlights={flightInfo}></Flights> : <></>}
        </div>
    )
}

export default FlightSearch;
